mapper.select:
SELECT `o_id` oId, `app_id` appId, `ext_transaction_id` extTransactionId, `user_id` userId, `request_time`
requestTime,
`create_time` createTime,`order_type_cd` orderTypeCd, `finish_time` finishTime, `remark`,t.status_cd statusCd
from c_orders t
where 1=1

    and t.o_id = #{oId}


    and t.ext_transaction_id = #{extTransactionId}


    and t.app_id = #{appId}

select t.b_id bId,t.o_id oId,t.create_time createTime,t.action,t.action_obj actionObj, t.finish_time finishTime,t.remark,ul.service_name serviceName,ul.log_text logText from c_order_item t LEFT JOIN unitem_log ul on t.b_id = ul.b_id and t.o_id = ul.o_id and ul.status_cd = '0' where 1=1
and t.o_id = #{oId}
and t.b_id = #{bId}

mapper.update:
update c_order_item t
set t.finish_time = #{finishTime},
t.status_cd = #{statusCd}
where t.o_id = #{oId}

    and t.b_id = #{bId}



update c_orders co set
co.status_cd=#{statusCd},
co.finish_time=#{finishTime}
where co.o_id=#{oId}

update c_business cb set
cb.status_cd=#{statusCd},
cb.finish_time=#{finishTime}
where cb.o_id=#{oId}



update c_orders co set co.status_cd = 'C' where co.status_cd='S'
    and co.o_id = #{oId}
    and not exists(
            select 1 from c_business cb where cb.status_cd <> 'C'
            and cb.o_id = co.o_id
    )

mapper.select:


select co.* from c_orders co where 1 = 1 and exists
(
    select 1 from c_business cb where cb.o_id = co.o_id
    and cb.b_id = #{bId}
)

select distinct co.app_id appId,co.o_id oId,co.create_time createTime, co.ext_transaction_id extTransactionId,co.user_id userId,cb.b_id bId from c_orders co INNER JOIN c_business cb on co.o_id = cb.o_id where 1=1
cb.b_id = #{bId}
and cb.b_id in
#{item}

SELECT co.`o_id`,co.`app_id`,co.`order_type_cd`,co.`user_id`,cb.`business_type_cd`,cb.`b_id`,cb.`status_cd`
FROM c_orders co,c_business cb
WHERE co.`o_id` = cb.`o_id`
AND cb.`business_type_cd` NOT IN ('DO')

    AND cb.`status_cd` = #{statusCd}


    AND co.`o_id` = #{oId}



select * from c_business cb where cb.finish_time is not null
and cb.o_id in (
    select cb1.o_id from c_business cb1 where cb1.b_id = #{bId}
)

select cb.b_id bId,co.o_id oId,cb.business_type_cd businessTypeCd from c_orders co,c_business cb where co.o_id = cb.o_id and cb.status_cd = 'C'
and cb.business_type_cd in
#{item}

SELECT cb1.b_id bId, cb1.o_id oId, cb1.create_time createTime, cb1.business_type_cd businessTypeCd, cb1.finish_time finishTime, cb1.status_cd statusCd FROM c_business cb1
,c_business cb2
WHERE 1=1
and cb1.o_id = cb2.o_id AND cb2.b_id = #{bId}
and cb1.o_id = #{oId}
and cb1.business_type_cd = #{businessTypeCd}
