    , t.remark= #{remark}


    , t.face_path= #{facePath}


    , t.state= #{state}


    , t.store_id= #{storeId}


    , t.value= #{value}


    , t.task_id= #{taskId}

where 1=1

    and t.detail_id= #{detailId}

mapper.select:
