mapper.select:
select t.floor_id,t.operate,t.name,t.remark,t.b_id,t.user_id,t.floor_num,t.community_id,t.floor_area
from business_floor t
where 1 =1

    and t.floor_id= #{floorId}


    and t.operate= #{operate}


    and t.name= #{name}


    and t.remark= #{remark}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.floor_num= #{floorNum}


    and t.community_id= #{communityId}

mapper.insert:
insert into f_floor(
floor_id,name,status_cd,remark,b_id,user_id,floor_num,community_id,floor_area
) select t.floor_id,t.name,'0',t.remark,t.b_id,t.user_id,t.floor_num,t.community_id,t.floor_area
from business_floor t where 1=1

    and t.floor_id= #{floorId}

and t.operate= 'ADD'

    and t.name= #{name}


    and t.remark= #{remark}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.floor_num= #{floorNum}


    and t.community_id= #{communityId}

mapper.select:
select t.floor_id,t.floor_id floorId,t.name,t.status_cd, t.status_cd statusCd,t.remark,t.b_id,
t.b_id bId,t.user_id, t.user_id userId,t.floor_num ,t.floor_num floorNum,t.community_id communityId,t.community_id,
t.floor_area,t.floor_area floorArea
from f_floor t
where 1 =1

    and t.floor_id= #{floorId}


    and t.name= #{name}


    and t.status_cd= #{statusCd}


    and t.remark= #{remark}


    and t.b_id= #{bId}


    and t.user_id= #{userId}


    and t.floor_num= #{floorNum}


    and t.community_id= #{communityId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update f_floor t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.name= #{name}


    , t.floor_area= #{floorArea}


    , t.remark= #{remark}


    , t.user_id= #{userId}


    , t.floor_num= #{floorNum}

where 1=1

    and t.floor_id= #{floorId}


    and t.community_id= #{communityId}


    and t.b_id= #{bId}

mapper.select:
SELECT
COUNT(1) count
FROM
f_floor f
where 1=1

    and f.`community_id` = #{communityId}


    and f.floor_id= #{floorId}


    and f.floor_id in

        #{item}



    and f.name like concat('%',#{name},'%')


    and f.name like concat('%',#{floorName},'%')


    and f.status_cd= #{statusCd}


    and f.remark= #{remark}


    and f.b_id= #{bId}



    and f.floor_num= #{floorNum}

AND f.`status_cd` = '0'

SELECT
f.`floor_id` floorId,
f.`floor_num` floorNum,
f.`name` floorName,
f.`remark`,
f.`user_id` userId,
f.`community_id` communityId,
f.floor_area floorArea
FROM
f_floor f
where 1=1

    and f.`community_id` = #{communityId}


    and f.floor_id= #{floorId}


    and f.floor_id in

        #{item}



    and f.name like concat('%',#{name},'%')


    and f.name like concat('%',#{floorName},'%')


    and f.status_cd= #{statusCd}


    and f.remark= #{remark}


    and f.b_id= #{bId}


    and f.floor_num= #{floorNum}

AND f.`status_cd` = '0'
order by f.create_time desc

    LIMIT #{page}, #{row}

select t.floor_id,t.floor_id floorId,bu.layer_count,bu.layer_count layerCount,bu.unit_id,bu.unit_id
unitId,bu.unit_num,bu.unit_num unitNum,bu.lift,bu.remark,bu.unit_area,bu.unit_area unitArea,t.floor_num floorNum
from f_floor t
left join building_unit bu on t.`floor_id` = bu.`floor_id` and bu.status_cd = '0'
where 1 =1
and t.status_cd = '0'

    AND t.community_id = #{communityId}


    and t.floor_id= #{floorId}


    and bu.layer_count= #{layerCount}


    and bu.unit_id= #{unitId}


    and bu.unit_num= #{unitNum}


    and bu.lift= #{lift}

order by unitNum asc
