mapper.select:
select t.operate,t.domain,t.name,t.remark,t.id,t.value,t.key
from business_mapping t
where 1 =1

    and t.operate= #{operate}


    and t.domain= #{domain}


    and t.name= #{name}


    and t.remark= #{remark}


    and t.id= #{id}


    and t.value= #{value}


    and t.`key`= #{key}

mapper.insert:
insert into c_mapping(
domain,name,remark,status_cd,id,value,key
) select t.domain,t.name,t.remark,'0',t.id,t.value,t.key from business_mapping t where 1=1
and t.operate= 'ADD'

    and t.domain= #{domain}


    and t.name= #{name}


    and t.remark= #{remark}


    and t.id= #{id}


    and t.value= #{value}


    and t.key= #{key}

mapper.select:
select t.domain,t.name,t.remark,t.status_cd,t.status_cd statusCd,t.id,t.value,t.key
from c_mapping t
where 1 =1

    and t.domain= #{domain}


    and t.name= #{name}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.id= #{id}


    and t.value= #{value}


    and t.key= #{key}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update c_mapping t set t.status_cd = #{statusCd}


    , t.domain= #{domain}


    , t.name= #{name}


    , t.remark= #{remark}


    , t.value= #{value}


    , t.`key`= #{key}

where 1=1

    and t.id= #{id}

mapper.select:
select count(1) count
from c_mapping t
where 1 =1

    and t.domain= #{domain}


    and t.name= #{name}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.id= #{id}


    and t.value= #{value}


    and t.key= #{key}
