mapper.select:
select t.default_area,t.default_area defaultArea,t.ba_id,t.ba_id baId,t.pa_id,t.pa_id
paId,t.remark,t.status_cd,t.status_cd statusCd,t.community_id,t.community_id communityId,t.box_id,t.box_id boxId,
pa.num paNum,t.create_time createTime
from parking_box_area t
left join parking_area pa on t.pa_id = pa.pa_id and t.community_id = pa.community_id and pa.status_cd = '0'
where 1 =1

    and t.default_area= #{defaultArea}


    and t.ba_id= #{baId}


    and t.pa_id= #{paId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.box_id= #{boxId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update parking_box_area t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.default_area= #{defaultArea}


    , t.pa_id= #{paId}


    , t.remark= #{remark}


where 1=1

    and t.ba_id= #{baId}


    and t.community_id= #{communityId}


    and t.box_id= #{boxId}

mapper.select:
select count(1) count
from parking_box_area t
where 1 =1

    and t.default_area= #{defaultArea}


    and t.ba_id= #{baId}


    and t.pa_id= #{paId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.box_id= #{boxId}
