mapper.select:
        select  t.area,t.parking_type,t.parking_type parkingType,t.num,t.pa_id,t.pa_id paId,t.ps_id,t.ps_id psId,t.remark,t.status_cd,t.status_cd statusCd,t.state,t.community_id,t.community_id communityId 
from parking_space t 
where 1 =1 

   and t.area= #{area}


   and t.parking_type= #{parkingType}


   and t.num= #{num}


   and t.pa_id= #{paId}


   and t.ps_id= #{psId}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.state= #{state}


   and t.community_id= #{communityId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  parking_space t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.area= #{area}


, t.parking_type= #{parkingType}


, t.num= #{num}


, t.pa_id= #{paId}


, t.remark= #{remark}


, t.state= #{state}


, t.community_id= #{communityId}

 where 1=1 
and t.ps_id= #{psId}

mapper.select:
        select  count(1) count 
from parking_space t 
where 1 =1 

   and t.area= #{area}


   and t.parking_type= #{parkingType}


   and t.num= #{num}


   and t.pa_id= #{paId}


   and t.ps_id= #{psId}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.state= #{state}


   and t.community_id= #{communityId}
