mapper.select:
        select  t.detail_type,t.detail_type detailType,t.staff_name,t.staff_name staffName,t.detail_id,t.detail_id detailId,t.status_cd,t.status_cd statusCd,t.remark,t.state,t.r_id,t.r_id rId,t.community_id,t.community_id communityId,t.staff_id,t.staff_id staffId 
from room_renovation_detail t 
where 1 =1 

   and t.detail_type= #{detailType}


   and t.staff_name= #{staffName}


   and t.detail_id= #{detailId}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.state= #{state}


   and t.r_id= #{rId}


   and t.community_id= #{communityId}


   and t.staff_id= #{staffId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  room_renovation_detail t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.detail_type= #{detailType}


, t.staff_name= #{staffName}


, t.remark= #{remark}


, t.state= #{state}


, t.r_id= #{rId}


, t.community_id= #{communityId}


, t.staff_id= #{staffId}

 where 1=1 
and t.detail_id= #{detailId}

mapper.select:
        select  count(1) count 
from room_renovation_detail t 
where 1 =1 

   and t.detail_type= #{detailType}


   and t.staff_name= #{staffName}


   and t.detail_id= #{detailId}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.state= #{state}


   and t.r_id= #{rId}


   and t.community_id= #{communityId}


   and t.staff_id= #{staffId}
