mapper.select:
select t.member_id,t.member_id memberId,pfc.fee_type_cd,pfc.fee_type_cd feeTypeCd,pfc.computing_formula,pfc.computing_formula computingFormula,pfc.additional_amount,pfc.additional_amount additionalAmount,pfc.status_cd,pfc.status_cd statusCd,pfc.square_price,pfc.square_price squarePrice,pfc.is_default,pfc.is_default isDefault,pfc.config_id,pfc.config_id configId,pfc.fee_flag,pfc.fee_flag feeFlag,pfc.fee_name,pfc.fee_name feeName,pfc.start_time,pfc.start_time startTime,pfc.end_time,pfc.end_time endTime,pfc.community_id,pfc.community_id communityId,pfc.b_id,pfc.b_id bId, td1.name feeTypeCdName,td2.name feeFlagName,pfc.bill_type billType,pfc.bill_type,td3.name billTypeName, pfc.payment_cd,pfc.payment_cycle,pfc.payment_cd paymentCd,pfc.payment_cycle paymentCycle, pfc.computing_formula_text,pfc.computing_formula_text computingFormulaText from fee_combo_member t, pay_fee_config pfc,t_dict td1,t_dict td2,t_dict td3 where 1 =1 and t.config_id = pfc.config_id and pfc.fee_type_cd = td1.status_cd and td1.table_name = 'pay_fee_config' and td1.table_columns = 'fee_type_cd' and pfc.fee_flag = td2.status_cd and td2.table_name = 'pay_fee_config' and td2.table_columns = 'fee_flag' and pfc.bill_type = td3.status_cd and td3.table_name = 'pay_fee_config' and td3.table_columns = 'bill_type'
and t.config_id= #{configId}
and t.combo_id= #{comboId}
and t.status_cd= #{statusCd}
and t.community_id= #{communityId}
and t.member_id= #{memberId}
order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update fee_combo_member t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.config_id= #{configId}


    , t.community_id= #{communityId}


where 1=1

    and t.combo_id= #{comboId}


    and t.member_id= #{memberId}

mapper.select:
select count(1) count from fee_combo_member t, pay_fee_config pfc,t_dict td1,t_dict td2,t_dict td3 where 1 =1 and t.config_id = pfc.config_id and pfc.fee_type_cd = td1.status_cd and td1.table_name = 'pay_fee_config' and td1.table_columns = 'fee_type_cd' and pfc.fee_flag = td2.status_cd and td2.table_name = 'pay_fee_config' and td2.table_columns = 'fee_flag' and pfc.bill_type = td3.status_cd and td3.table_name = 'pay_fee_config' and td3.table_columns = 'bill_type'
and t.config_id= #{configId}
and t.combo_id= #{comboId}
and t.status_cd= #{statusCd}
and t.community_id= #{communityId}
and t.member_id= #{memberId}
