mapper.select:
        select  t.msg,t.create_user_id,t.create_user_id createUserId,t.create_user_name,t.create_user_name createUserName,t.status_cd,t.status_cd statusCd,t.remark,t.state,t.batch_id,t.batch_id batchId,t.community_id,t.community_id communityId 
from pay_fee_batch t 
where 1 =1 

   and t.msg= #{msg}


   and t.create_user_id= #{createUserId}


   and t.create_user_name= #{createUserName}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.state= #{state}


   and t.batch_id= #{batchId}


   and t.community_id= #{communityId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  pay_fee_batch t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.msg= #{msg}


, t.create_user_id= #{createUserId}


, t.create_user_name= #{createUserName}


, t.remark= #{remark}


, t.state= #{state}


, t.community_id= #{communityId}

 where 1=1 
and t.batch_id= #{batchId}

mapper.select:
        select  count(1) count 
from pay_fee_batch t 
where 1 =1 

   and t.msg= #{msg}


   and t.create_user_id= #{createUserId}


   and t.create_user_name= #{createUserName}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.state= #{state}


   and t.batch_id= #{batchId}


   and t.community_id= #{communityId}
