mapper.update:
update saop_ftp_task sft
set update_date = sysdate
,sft.taskname = #{taskName}

,sft.file_name = #{fileName}

,sft.u_or_d = #{uOrD}
,sft.run_state = #{runState}

,sft.run_flag = #{runFlag}
,sft.tnum = #{tNum}
,sft.taskcron = #{taskCron}

,sft.errphone = #{errPhone}

,sft.fileseq = #{fileSeq}
,sft.upnull = #{upNull}
,sft.preflag = #{preFlag}
,sft.prefunction = #{preFunction}

,sft.afterflag = #{afterFlag}

,sft.afterfunction = #{afterFunction}

,sft.deal_class = #{dealClass}

where sft.state = 'A'
and sft.taskid = #{taskId}

update
saop_ftp_task sft
set update_date = sysdate,sft.run_state= #{runState}
where sft.run_state = #{oldRunState}

mapper.select:
SELECT A.TASKID, A.ITEM_SPEC_ID, A.VALUE
FROM SAOP_FTP_TASK_ATTR A
WHERE A.TASKID = #{taskId}

select a.item_spec_cd, a.domain, a.name, a.describe, a.create_date
from saop_ftp_item_spec a
where a.status_cd = '0'
 and a.domain = #{domain}
  and a.name = #{name}
  and a.item_spec_cd = #i{temSpecCd}

mapper.delete:
DELETE
FROM SAOP_FTP_TASK_ATTR A
WHERE A.TASKID = #{taskId}

mapper.update:
update cep_ftp_tasklog set update_date=now()
,state = #{state}
where logid=#{logid}

update cep_ftp_tasklog_detail set update_date=now()
,state = #{state}
,data = #{data}
,havedown = #{havedown}
,remark = #{remark}
where id=#id#

mapper.select:
select distinct column_name,data_type from all_tab_columns where table_name=#tablename#
owner=#{username}
column_name in($colnames$)
