mapper.select:
        select  t.group_id,t.group_id groupId,t.remark,t.status_cd,t.status_cd statusCd,t.title,t.custom_id,t.custom_id customId,t.seq 
from report_custom t 
where 1 =1 

   and t.group_id= #{groupId}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.title= #{title}


   and t.custom_id= #{customId}


   and t.seq= #{seq}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  report_custom t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.group_id= #{groupId}


, t.remark= #{remark}


, t.title= #{title}


, t.seq= #{seq}

 where 1=1 
and t.custom_id= #{customId}

mapper.select:
        select  count(1) count 
from report_custom t 
where 1 =1 

   and t.group_id= #{groupId}


   and t.remark= #{remark}


   and t.status_cd= #{statusCd}


   and t.title= #{title}


   and t.custom_id= #{customId}


   and t.seq= #{seq}
