mapper.select:
select t.pf_year pfYear,t.pf_month pfMonth ,t.amount,t.fee_id feeId
from report_owner_pay_fee t
where t.status_cd = '0'

    and t.fee_id= #{feeId}


    and t.pf_year= #{pfYear}


    and t.community_id= #{communityId}


    and t.fee_id in

        #{item}

select t.community_id communityId,pfa.`value` ownerId,pfa1.`value` ownerName,pfc.fee_name feeName,pfa2.`value` importFeeName,t.config_id configId,t.fee_id feeId, concat(ff.floor_num , '-' , bu.unit_num , '-',br.room_num) roomName,t.payer_obj_type payerObjType,oc.car_num carNum,con.contract_code contractCode from pay_fee t inner join pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.community_id = t.community_id and pfa.spec_cd = '390007' and pfa.status_cd = '0' inner join pay_fee_attrs pfa1 on t.fee_id = pfa1.fee_id and pfa1.community_id = t.community_id and pfa1.spec_cd = '390008' and pfa1.status_cd = '0' left join pay_fee_attrs pfa2 on t.fee_id = pfa2.fee_id and pfa2.community_id = t.community_id and pfa2.spec_cd = '390002' and pfa2.status_cd = '0' inner join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.community_id = t.community_id and pfc.status_cd = '0' LEFT JOIN building_room br on t.payer_obj_id = br.room_id and t.payer_obj_type = '3333' and br.status_cd = '0' LEFT JOIN building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' LEFT JOIN f_floor ff on bu.floor_id = ff.floor_id and ff.status_cd = '0' left join owner_car oc on t.payer_obj_id = oc.car_id and t.payer_obj_type = '6666' and oc.status_cd = '0' and oc.car_type_cd='1001' left join contract con on t.payer_obj_id = con.contract_id and t.payer_obj_type = '7777' and con.status_cd = '0' where t.end_time > NOW() and t.state = '2008001' and t.status_cd = '0'
and t.config_id= #{configId}
and t.community_id= #{communityId}
and pfa1.`value` = #{ownerName}
and t.fee_id= #{feeId}
order by t.create_time desc
limit #{page}, #{row}

mapper.update:
update report_owner_pay_fee t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.config_name= #{configName}


    , t.amount= #{amount}


    , t.detail_id= #{detailId}


    , t.obj_name= #{objName}


    , t.owner_id= #{ownerId}


    , t.fee_id= #{feeId}


    , t.pf_month= #{pfMonth}


    , t.owner_name= #{ownerName}


    , t.pf_year= #{pfYear}


    , t.config_id= #{configId}


    , t.pf_date= #{pfDate}


    , t.obj_id= #{objId}


    , t.fee_name= #{feeName}


    , t.community_id= #{communityId}


    , t.obj_type= #{objType}

where 1=1

    and t.pf_id= #{pfId}

mapper.select:
select count(1) count from pay_fee t inner join pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.community_id = t.community_id and pfa.spec_cd = '390007' and pfa.status_cd = '0' inner join pay_fee_attrs pfa1 on t.fee_id = pfa1.fee_id and pfa1.community_id = t.community_id and pfa1.spec_cd = '390008' and pfa1.status_cd = '0' left join pay_fee_attrs pfa2 on t.fee_id = pfa2.fee_id and pfa2.community_id = t.community_id and pfa2.spec_cd = '390002' and pfa2.status_cd = '0' inner join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.community_id = t.community_id and pfc.status_cd = '0' LEFT JOIN building_room br on t.payer_obj_id = br.room_id and t.payer_obj_type = '3333' and br.status_cd = '0' LEFT JOIN building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0' LEFT JOIN f_floor ff on bu.floor_id = ff.floor_id and ff.status_cd = '0' left join owner_car oc on t.payer_obj_id = oc.car_id and t.payer_obj_type = '6666' and oc.status_cd = '0' and oc.car_type_cd='1001' left join contract con on t.payer_obj_id = con.contract_id and t.payer_obj_type = '7777' and con.status_cd = '0' where t.end_time > NOW() and t.state = '2008001' and t.status_cd = '0'
and t.config_id= #{configId}
and t.community_id= #{communityId}
and pfa1.`value` = #{ownerName}
and t.fee_id= #{feeId}
