mapper.select:
select t.as_id,t.as_id asId,t.store_id,t.store_id storeId,t.res_id,t.res_id resId,t.sh_id_z,t.sh_id_z
shIdz,t.res_name,t.res_name resName,t.start_user_id,t.start_user_id startUserId,t.operate,t.sh_id_a,t.sh_id_a
shIda,t.start_user_name,t.start_user_name startUserName,t.state,t.b_id,t.b_id bId,t.stock,t.remark
from business_allocation_storehouse t
where 1 =1

    and t.as_id= #{asId}


    and t.store_id= #{storeId}


    and t.res_id= #{resId}


    and t.sh_id_z= #{shIdz}


    and t.res_name= #{resName}


    and t.start_user_id= #{startUserId}


    and t.operate= #{operate}


    and t.sh_id_a= #{shIda}


    and t.start_user_name= #{startUserName}


    and t.state= #{state}


    and t.b_id= #{bId}


    and t.stock= #{stock}

mapper.insert:
insert into allocation_storehouse(
as_id,status_cd,store_id,res_id,sh_id_z,res_name,start_user_id,sh_id_a,start_user_name,state,b_id,stock,remark
) select
t.as_id,'0',t.store_id,t.res_id,t.sh_id_z,t.res_name,t.start_user_id,t.sh_id_a,t.start_user_name,t.state,t.b_id,t.stock,t.remark
from business_allocation_storehouse t where 1=1

    and t.as_id= #{asId}


    and t.store_id= #{storeId}


    and t.res_id= #{resId}


    and t.sh_id_z= #{shIdz}


    and t.res_name= #{resName}


    and t.start_user_id= #{startUserId}

and t.operate= 'ADD'

    and t.sh_id_a= #{shIda}


    and t.start_user_name= #{startUserName}


    and t.state= #{state}


    and t.b_id= #{bId}


    and t.stock= #{stock}

mapper.select:
select t.as_id,t.as_id asId,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.res_id,t.res_id
resId,t.sh_id_z,t.sh_id_z shIdz,t.res_name,t.res_name resName,t.start_user_id,t.start_user_id
startUserId,t.sh_id_a,t.sh_id_a shIda,t.start_user_name,t.start_user_name startUserName,t.state,t.b_id,t.b_id
bId,t.stock,t.remark,t.create_time createTime,td.name stateName,a.sh_name shaName,z.sh_name shzName,
rs.res_code,rs.res_code resCode
from allocation_storehouse t
left join t_dict td on t.state = td.status_cd and td.table_name = 'allocation_storehouse' and td.table_columns =
'state'
left join storehouse a on t.sh_id_a = a.sh_id and a.store_id = t.store_id and a.status_cd = '0'
left join storehouse z on t.sh_id_z = z.sh_id and z.store_id = t.store_id and z.status_cd = '0'
left join resource_store rs on t.res_id = rs.res_id and rs.status_cd = '0'
where 1 = 1

    and t.as_id= #{asId}


    and t.as_id in

        #{item}



    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.res_id= #{resId}


    and t.sh_id_z= #{shIdz}


    and t.res_name like '%${resName}%'


    and t.start_user_id= #{startUserId}


    and t.sh_id_a= #{shIda}


    and t.start_user_name= #{startUserName}


    and t.state= #{state}


    and t.b_id= #{bId}


    and t.stock= #{stock}


    and rs.res_code= #{resCode}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update allocation_storehouse t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.store_id= #{storeId}


    , t.res_id= #{resId}


    , t.sh_id_z= #{shIdz}


    , t.res_name= #{resName}


    , t.start_user_id= #{startUserId}


    , t.sh_id_a= #{shIda}


    , t.start_user_name= #{startUserName}


    , t.state= #{state}


    , t.stock= #{stock}

where 1=1

    and t.as_id= #{asId}


    and t.b_id= #{bId}

mapper.select:
select count(1) count
from allocation_storehouse t
left join t_dict td on t.state = td.status_cd and td.table_name = 'allocation_storehouse' and td.table_columns =
'state'
left join storehouse a on t.sh_id_a = a.sh_id and a.store_id = t.store_id and a.status_cd = '0'
left join storehouse z on t.sh_id_z = z.sh_id and z.store_id = t.store_id and z.status_cd = '0'
left join resource_store rs on t.res_id = rs.res_id and rs.status_cd = '0'
where 1 = 1

    and t.as_id= #{asId}


    and t.as_id in

        #{item}



    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.res_id= #{resId}


    and t.sh_id_z= #{shIdz}


    and t.res_name like '%${resName}%'


    and t.start_user_id= #{startUserId}


    and t.sh_id_a= #{shIda}


    and t.start_user_name= #{startUserName}


    and t.state= #{state}


    and t.b_id= #{bId}


    and t.stock= #{stock}


    and rs.res_code= #{resCode}
