mapper.select:
        select  t.rel_id,t.rel_id relId,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.org_id,t.org_id orgId,t.staff_id,t.staff_id staffId,t.rel_cd,t.rel_cd relCd 
from u_org_staff_rel t 
where 1 =1 

   and t.rel_id= #{relId}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.org_id= #{orgId}


   and t.staff_id= #{staffId}


   and t.rel_cd= #{relCd}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  u_org_staff_rel t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.store_id= #{storeId}


, t.org_id= #{orgId}


, t.staff_id= #{staffId}


, t.rel_cd= #{relCd}

 where 1=1 
and t.rel_id= #{relId}

mapper.select:
        select  count(1) count 
from u_org_staff_rel t 
where 1 =1 

   and t.rel_id= #{relId}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.org_id= #{orgId}


   and t.staff_id= #{staffId}


   and t.rel_cd= #{relCd}
