mapper.select:
select t.rss_id,t.rss_id rssId,t.rst_id,t.rst_id rstId,t.operate,t.spec_name,t.spec_name
specName,t.description,t.b_id,t.b_id bId,t.store_id,t.store_id storeId,t.parent_rst_id,t.parent_rst_id parentRstId
from business_resource_store_specification t
where 1 =1

    and t.rss_id= #{rssId}


    and t.rst_id= #{rstId}


    and t.parent_rst_id= #{parentRstId}


    and t.operate= #{operate}


    and t.spec_name= #{specName}


    and t.description= #{description}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}

mapper.insert:
insert into resource_store_specification(
rss_id,rst_id,spec_name,description,status_cd,b_id,store_id,parent_rst_id
) select t.rss_id,t.rst_id,t.spec_name,t.description,'0',t.b_id,t.store_id,t.parent_rst_id from
business_resource_store_specification t where 1=1

    and t.rss_id= #{rssId}


    and t.rst_id= #{rstId}


    and t.parent_rst_id= #{parentRstId}

and t.operate= 'ADD'

    and t.spec_name= #{specName}


    and t.description= #{description}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}

mapper.select:
select t.rss_id,t.rss_id rssId,t.rst_id,t.rst_id rstId,t.spec_name,t.spec_name
specName,t.description,t.status_cd,t.status_cd statusCd,t.b_id,t.b_id bId,t.store_id,t.store_id
storeId,rst.name,rst.name rstName,t.parent_rst_id,t.parent_rst_id parentRstId,rst1.name parentRstName
from resource_store_specification t
left join resource_store_type rst on t.rst_id=rst.rst_id
left join resource_store_type rst1 on t.parent_rst_id=rst1.rst_id
where 1 =1

    and t.rss_id= #{rssId}


    and t.rst_id= #{rstId}


    and t.parent_rst_id= #{parentRstId}


    and t.spec_name like '%${specName}%'


    and t.description= #{description}


    and t.status_cd= #{statusCd}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update resource_store_specification t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.rst_id= #{rstId}


    , t.parent_rst_id= #{parentRstId}


    , t.spec_name= #{specName}


    , t.description= #{description}


    , t.store_id= #{storeId}

where 1=1

    and t.rss_id= #{rssId}


    and t.b_id= #{bId}

mapper.select:
select count(1) count
from resource_store_specification t left join resource_store_type rst on t.rst_id=rst.rst_id
where 1 =1

    and t.rss_id= #{rssId}


    and t.rst_id= #{rstId}


    and t.parent_rst_id= #{parentRstId}


    and t.spec_name like '%${specName}%'


    and t.description= #{description}


    and t.status_cd= #{statusCd}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}
