mapper.select:
select t.rst_id,t.rst_id rstId,t.name,t.description,t.b_id,t.b_id bId,t.store_id,t.store_id
storeId,t.goods_type,t.goods_type goodsType
from business_resource_store_type t
where 1 =1

    and t.rst_id= #{rstId}


    and t.name= #{name}


    and t.description= #{description}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}


    and t.goods_type= #{goodsType}

mapper.insert:
insert into resource_store_type(
rst_id,name,description,status_cd,b_id,store_id,goods_type,create_time
) select t.rst_id,t.name,t.description,'0',t.b_id,t.store_id,t.goods_type,t.create_time from
business_resource_store_type t
where 1=1

    and t.rst_id= #{rstId}


    and t.name= #{name}


    and t.description= #{description}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}


    and t.goods_type= #{goodsType}

mapper.select:
select t.rst_id,t.rst_id rstId,t.name,t.description,t.status_cd,t.status_cd statusCd,t.b_id,t.b_id
bId,t.store_id,t.store_id storeId,t.goods_type,t.goods_type goodsType,t.create_time,t.create_time createTime
from resource_store_type t
where 1 =1

    and t.rst_id= #{rstId}


    and t.name like '%${name}%'


    and t.description= #{description}


    and t.status_cd= #{statusCd}


    and t.b_id= #{bId}


    and t.store_id= #{storeId}


    and t.goods_type= #{goodsType}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update resource_store_type t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.name= #{name}


