mapper.select:
        select  t.rst_id,t.rst_id rstId,t.name,t.description,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.parent_id,t.parent_id parentId 
from resource_store_type t 
where 1 =1 

   and t.rst_id= #{rstId}


   and t.name= #{name}


   and t.description= #{description}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.parent_id= #{parentId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  resource_store_type t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.name= #{name}


, t.description= #{description}


, t.store_id= #{storeId}


, t.parent_id= #{parentId}

 where 1=1 
and t.rst_id= #{rstId}

mapper.select:
        select  count(1) count 
from resource_store_type t 
where 1 =1 

   and t.rst_id= #{rstId}


   and t.name= #{name}


   and t.description= #{description}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.parent_id= #{parentId}
