mapper.select:
        select  t.sh_id,t.sh_id shId,t.description,t.status_cd,t.status_cd statusCd,t.remark,t.store_id,t.store_id storeId,t.res_id,t.res_id resId,t.parent_rst_id,t.parent_rst_id parentRstId,t.warning_stock,t.warning_stock warningStock,t.rss_id,t.rss_id rssId,t.res_name,t.res_name resName,t.rst_id,t.rst_id rstId,t.out_low_price,t.out_low_price outLowPrice,t.price,t.mini_unit_stock,t.mini_unit_stock miniUnitStock,t.unit_code,t.unit_code unitCode,t.res_code,t.res_code resCode,t.mini_unit_code,t.mini_unit_code miniUnitCode,t.average_price,t.average_price averagePrice,t.stock,t.out_high_price,t.out_high_price outHighPrice,t.show_mobile,t.show_mobile showMobile,t.is_fixed,t.is_fixed isFixed,t.mini_stock,t.mini_stock miniStock 
from resource_store t 
where 1 =1 

   and t.sh_id= #{shId}


   and t.description= #{description}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.store_id= #{storeId}


   and t.res_id= #{resId}


   and t.parent_rst_id= #{parentRstId}


   and t.warning_stock= #{warningStock}


   and t.rss_id= #{rssId}


   and t.res_name= #{resName}


   and t.rst_id= #{rstId}


   and t.out_low_price= #{outLowPrice}


   and t.price= #{price}


   and t.mini_unit_stock= #{miniUnitStock}


   and t.unit_code= #{unitCode}


   and t.res_code= #{resCode}


   and t.mini_unit_code= #{miniUnitCode}


   and t.average_price= #{averagePrice}


   and t.stock= #{stock}


   and t.out_high_price= #{outHighPrice}


   and t.show_mobile= #{showMobile}


   and t.is_fixed= #{isFixed}


   and t.mini_stock= #{miniStock}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  resource_store t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.sh_id= #{shId}


, t.description= #{description}


, t.remark= #{remark}


, t.store_id= #{storeId}


, t.parent_rst_id= #{parentRstId}


, t.warning_stock= #{warningStock}


, t.rss_id= #{rssId}


, t.res_name= #{resName}


, t.rst_id= #{rstId}


, t.out_low_price= #{outLowPrice}


, t.price= #{price}


, t.mini_unit_stock= #{miniUnitStock}


, t.unit_code= #{unitCode}


, t.res_code= #{resCode}


, t.mini_unit_code= #{miniUnitCode}


, t.average_price= #{averagePrice}


, t.stock= #{stock}


, t.out_high_price= #{outHighPrice}


, t.show_mobile= #{showMobile}


, t.is_fixed= #{isFixed}


, t.mini_stock= #{miniStock}

 where 1=1 
and t.res_id= #{resId}

mapper.select:
        select  count(1) count 
from resource_store t 
where 1 =1 

   and t.sh_id= #{shId}


   and t.description= #{description}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.store_id= #{storeId}


   and t.res_id= #{resId}


   and t.parent_rst_id= #{parentRstId}


   and t.warning_stock= #{warningStock}


   and t.rss_id= #{rssId}


   and t.res_name= #{resName}


   and t.rst_id= #{rstId}


   and t.out_low_price= #{outLowPrice}


   and t.price= #{price}


   and t.mini_unit_stock= #{miniUnitStock}


   and t.unit_code= #{unitCode}


   and t.res_code= #{resCode}


   and t.mini_unit_code= #{miniUnitCode}


   and t.average_price= #{averagePrice}


   and t.stock= #{stock}


   and t.out_high_price= #{outHighPrice}


   and t.show_mobile= #{showMobile}


   and t.is_fixed= #{isFixed}


