mapper.select:
select
    t.attr_id,t.attr_id attrId,
    t.operate,
    t.create_time,t.create_time createTime,
    t.spec_cd,t.spec_cd specCd,
    t.store_id,t.store_id storeId,
    t.b_id,t.b_id bId,
    t.value
from business_store_attr t
where 1 =1

    and t.attr_id= #{attrId}


    and t.operate= #{operate}


    and t.create_time= #{createTime}


    and t.spec_cd= #{specCd}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.value= #{value}

mapper.insert:
insert into s_store_attr(
attr_id,create_time,spec_cd,status_cd,store_id,bId,value
) select t.attr_id,t.create_time,t.spec_cd,'0',t.store_id,t.bId,t.value from business_store_attr t where 1=1

    and t.attr_id= #{attrId}

and t.operate= 'ADD'

    and t.create_time= #{createTime}


    and t.spec_cd= #{specCd}


    and t.store_id= #{storeId}


    and t.bId= #{bId}


    and t.value= #{value}

mapper.select:
select
    t.attr_id,t.attr_id attrId,
    t.create_time,t.create_time createTime,
    t.spec_cd,t.spec_cd specCd,
    t.status_cd,t.status_cd statusCd,
    t.store_id,t.store_id storeId,
    t.b_id, t.b_id bId,
    t.value
from s_store_attr t
where 1 =1

    and t.attr_id= #{attrId}


    and t.create_time= #{createTime}


    and t.spec_cd= #{specCd}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.b_id= #{bId}


    and t.value= #{value}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update s_store_attr t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.spec_cd= #{specCd}


    , t.store_id= #{storeId}


    , t.value= #{value}

where 1=1

    and t.attr_id= #{attrId}


    and t.bId= #{bId}

mapper.select:
select count(1) count
from s_store_attr t
where 1 =1

    and t.attr_id= #{attrId}


    and t.create_time= #{createTime}


    and t.spec_cd= #{specCd}


    and t.status_cd= #{statusCd}


    and t.store_id= #{storeId}


    and t.bId= #{bId}


