mapper.select:
        select  t.attr_id,t.attr_id attrId,t.spec_cd,t.spec_cd specCd,t.status_cd,t.status_cd statusCd,t.store_id,t.store_id storeId,t.value 
from s_store_attr t 
where 1 =1 

   and t.attr_id= #{attrId}


   and t.spec_cd= #{specCd}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.value= #{value}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  s_store_attr t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.spec_cd= #{specCd}


, t.store_id= #{storeId}


, t.value= #{value}

 where 1=1 
and t.attr_id= #{attrId}

mapper.select:
        select  count(1) count 
from s_store_attr t 
where 1 =1 

   and t.attr_id= #{attrId}


   and t.spec_cd= #{specCd}


   and t.status_cd= #{statusCd}


   and t.store_id= #{storeId}


   and t.value= #{value}
