mapper.select:
        select  t.address,t.store_type_cd,t.store_type_cd storeTypeCd,t.name,t.tel,t.status_cd,t.status_cd statusCd,t.state,t.store_id,t.store_id storeId,t.user_id,t.user_id userId,t.map_y,t.map_y mapY,t.map_x,t.map_x mapX 
from s_store t 
where 1 =1 

   and t.address= #{address}


   and t.store_type_cd= #{storeTypeCd}


   and t.name= #{name}


   and t.tel= #{tel}


   and t.status_cd= #{statusCd}


   and t.state= #{state}


   and t.store_id= #{storeId}


   and t.user_id= #{userId}


   and t.map_y= #{mapY}


   and t.map_x= #{mapX}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  s_store t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.address= #{address}


, t.store_type_cd= #{storeTypeCd}


, t.name= #{name}


, t.tel= #{tel}


, t.state= #{state}


, t.user_id= #{userId}


, t.map_y= #{mapY}


, t.map_x= #{mapX}

 where 1=1 
and t.store_id= #{storeId}

mapper.select:
        select  count(1) count 
from s_store t 
where 1 =1 

   and t.address= #{address}


   and t.store_type_cd= #{storeTypeCd}


   and t.name= #{name}


   and t.tel= #{tel}


   and t.status_cd= #{statusCd}


   and t.state= #{state}


   and t.store_id= #{storeId}


   and t.user_id= #{userId}


   and t.map_y= #{mapY}


   and t.map_x= #{mapX}
