mapper.select:
        select  t.store_type,t.store_type storeType,t.ca_id,t.ca_id caId,t.name,t.icon,t.status_cd,t.status_cd statusCd,t.seq,t.url,t.is_show,t.is_show isShow 
from m_menu_catalog t 
where 1 =1 

   and t.store_type= #{storeType}


   and t.ca_id= #{caId}


   and t.name= #{name}


   and t.icon= #{icon}


   and t.status_cd= #{statusCd}


   and t.seq= #{seq}


   and t.url= #{url}


   and t.is_show= #{isShow}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  m_menu_catalog t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.store_type= #{storeType}


, t.name= #{name}


, t.icon= #{icon}


, t.seq= #{seq}


, t.url= #{url}


, t.is_show= #{isShow}

 where 1=1 
and t.ca_id= #{caId}

mapper.select:
        select  count(1) count 
from m_menu_catalog t 
where 1 =1 

   and t.store_type= #{storeType}


   and t.ca_id= #{caId}


   and t.name= #{name}


   and t.icon= #{icon}


   and t.status_cd= #{statusCd}


   and t.seq= #{seq}


   and t.url= #{url}


   and t.is_show= #{isShow}
